std::error::Error comes with some helper methods to get to the original object of the &(dyn Error + 'static) returned by .source(). pub fn downcast_ref<T: Error + 'static>(&self) -> Option<&T>
pub fn downcast_mut<T: Error + 'static>(&mut self) -> Option<&mut T> This is how it looks like, when using those: #![allow(clippy::single_match)]
#![allow(clippy::redundant_pattern_matching)] use chainerror::Context as _; use std::error::Error;
use std::io; fn do_some_io() -> Result<(), Box<dyn Error + Send + Sync>> { Err(io::Error::from(io::ErrorKind::NotFound))?; Ok(())
} fn func2() -> Result<(), Box<dyn Error + Send + Sync>> { let filename = "foo.txt"; do_some_io().context(format!("Error reading '{}'", filename))?; Ok(())
} fn func1() -> Result<(), Box<dyn Error + Send + Sync>> { func2().context("func1 error")?; Ok(())
} fn main() -> Result<(), Box<dyn Error + Send + Sync>> { if let Err(e) = func1() { eprintln!("Error: {}", e); let mut s: &(dyn Error) = e.as_ref(); while let Some(c) = s.source() { if let Some(ioerror) = c.downcast_ref::<io::Error>() { eprintln!("caused by: std::io::Error: {}", ioerror); match ioerror.kind() { io::ErrorKind::NotFound => eprintln!("of kind: std::io::ErrorKind::NotFound"), _ => {} } } else { eprintln!("caused by: {}", c); } s = c; } std::process::exit(1); } Ok(())
}
# #[allow(dead_code)]
# mod chainerror {
# #![doc = include_str!("../README.md")]
# #![deny(clippy::all)]
