# ///
# /// chainerror::str_context!(Func2Error);
# ///
# /// fn func2() -> Result<(), Box<dyn Error + Send + Sync>> {
# /// let filename = "foo.txt";
# /// do_some_io().context(Func2Error(format!("Error reading '{}'", filename)))?;
# /// Ok(())
# /// }
# ///
# /// #[derive(Debug)]
# /// enum Func1ErrorKind {
# /// Func2,
# /// IO(String),
# /// }
# ///
# /// /// impl ::std::fmt::Display for Func1ErrorKind {…}
