# /// Func1ErrorKind::Func2 => {}
# /// Func1ErrorKind::IO(filename) => panic!(),
# /// }
# /// }
# /// # else {
# /// # unreachable!();
# /// # }
# /// ```
# #[inline]
# pub fn kind(&self) -> &T {
# &self.kind
# }
# # /// Returns an Iterator over all error causes/sources
# ///
# /// # Example
# #[inline]
# pub fn iter(&self) -> impl Iterator<Item = &(dyn StdError + 'static)> {
# ErrorIter {
# current: Some(self),
# }
