# #[inline]
# pub fn find_cause<U: StdError + 'static>(&self) -> Option<&U> {
# self.iter()
# .filter_map(<dyn StdError>::downcast_ref::<U>)
# .next()
# }
# # /// Find the first error cause of type [`Error<U>`](Error), if any exists
