# #[inline]
# pub fn find_chain_cause<U: StdError + 'static>(&self) -> Option<&Error<U>> {
# self.iter()
# .filter_map(<dyn StdError>::downcast_ref::<Error<U>>)
# .next()
# }
# # /// Find the first error cause of type [`Error<U>`](Error) or `U`, if any exists and return `U`
