# /// # assert!(f1err.find_chain_cause::<Func2Error>().is_some());
# /// # } else {
# /// # panic!();
# /// # }
# /// # } else {
# /// # unreachable!();
# /// # }
# /// ```
# #[macro_export]
# macro_rules! str_context {
# ($e:ident) => {
# #[derive(Clone)]
# pub struct $e(pub String);
# impl $e {
# pub fn new<S: Into<String>>(s: S) -> Self {
# $e(s.into())
# }
