# /// do_some_io(filename).map_context(|e| ErrorKind::IO(filename.into()))?;
# /// do_some_io(filename).map_context(|e| ErrorKind::from(e))?;
# /// Ok(())
# /// }
# /// ```
# #[macro_export]
# macro_rules! err_kind {
# ($e:ident, $k:ident) => {
# pub struct $e($crate::Error<$k>);
# # impl $e {
# pub fn kind(&self) -> &$k {
