# # impl From<$k> for $e {
# fn from(e: $k) -> Self {
# $e($crate::Error::new(e, None, None))
# }
# }
# # impl From<$crate::Error<$k>> for $e {
# fn from(e: $crate::Error<$k>) -> Self {
# $e(e)
# }
# }
# # impl From<&$e> for $k
# where
