# self.0.source()
# }
# }
# # impl std::fmt::Display for $e {
# fn fmt(&self, f: &mut std::fmt::Formatter) -> std::fmt::Result {
# std::fmt::Display::fmt(&self.0, f)
# }
# }
# # impl std::fmt::Debug for $e {
# fn fmt(&self, f: &mut std::fmt::Formatter) -> std::fmt::Result {
# std::fmt::Debug::fmt(&self.0, f)
# }
# }
# };
# }
